  const powers={};
  switch(Id_Rol){
    case 1:
      powers.rendimiento=true;
      break;
    case 2:
      powers.salud=true
      break
    case 3:
      powers.rendimiento=true
      powers.salud=true
      powers.register=true
      break;
  }
  return powers;
}

